Add_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobiledatepicker_90': 'Add_mobiledatepicker_90',
        'mobilegrid_60': 'Add_mobilegrid_60',
        'mobilegridcell_61': 'Add_mobilegridcell_61',
        'mobilecheckboxgroup_65': 'Add_mobilecheckboxgroup_65',
        'mobilecheckbox_66': 'Add_mobilecheckbox_66',
        'mobilegridcell_62': 'Add_mobilegridcell_62',
        'mobiletextinput_71': 'Add_mobiletextinput_71',
        'mobilegrid_115': 'Add_mobilegrid_115',
        'mobilegridcell_116': 'Add_mobilegridcell_116',
        'mobilelabel_111': 'Add_mobilelabel_111',
        'mobilegridcell_117': 'Add_mobilegridcell_117',
        'mobilelabel_110': 'Add_mobilelabel_110',
        'mobilebutton_103': 'Add_mobilebutton_103',
        'mobilenavbar_72': 'Add_mobilenavbar_72',
        'mobilenavbaritem_73': 'Add_mobilenavbaritem_73',
        'mobilenavbaritem_74': 'Add_mobilenavbaritem_74',
        'mobilenavbaritem_75': 'Add_mobilenavbaritem_75'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'Add';

    /*
     * Nonvisual components
     */
    var datasources = [];

    restservice2 = new Appery.DataSource(PokerDB_Players_list_service, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("Add");
        },
        'onSuccess': function(data) {},
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [{
            'PATH': ['$'],
            'ID': 'mobilegrid_60',
            'SET': [{
                'PATH': ['Player'],
                'ID': 'mobilecheckbox_66',
                'ATTR': '@'
            }]
        }],
        'requestMapping': [{
            'PATH': ['X-Appery-Database-Id'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{database_id}'
        }]
    });

    datasources.push(restservice2);

    /*
     * Events and handlers
     */

    // Before Show
    Add_beforeshow = function() {
        Appery.CurrentScreen = "Add";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_56E8_onLoad = Add_onLoad = function() {
        screen_56E8_elementsExtraJS();
        onLoad();

        // TODO fire device events only if necessary (with JS logic)
        Add_deviceEvents();
        Add_windowEvents();
        screen_56E8_elementsEvents();
    }

    // screen window events
    screen_56E8_windowEvents = Add_windowEvents = function() {

        $('#Add').bind('pageshow orientationchange', function() {
            adjustContentHeightWithPadding();
        });
        $('#Add').bind({
            pageshow: function() {
                try {
                    restservice2.execute({})
                } catch (ex) {
                    console.log(ex.name + '  ' + ex.message);
                    hideSpinner();
                };
            },
        });

    }

    // device events
    Add_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_56E8_elementsExtraJS = Add_elementsExtraJS = function() {
        // screen (Add) extra code

        /* mobiledatepicker_90 */

        Add_mobiledatepicker_90_selector = "#Add_mobiledatepicker_90";
        Add_mobiledatepicker_90_dataPickerOptions = {
            dateFormat: "dd/mm/yy",
            firstDay: 0,

            maxDate: new Date(""),

            minDate: new Date(""),

            dayNamesMin: ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'],
            monthNames: ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'],
            showOtherMonths: true
        };

        Add_mobiledatepicker_90_dataPickerOptions.defaultDate = new Date();

        Appery.__registerComponent('mobiledatepicker_90', new Appery.ApperyMobileDatePickerComponent("Add_mobiledatepicker_90", Add_mobiledatepicker_90_dataPickerOptions));

    }

    // screen elements handler
    screen_56E8_elementsEvents = Add_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#Add_mobilecontainer [name="mobilecheckbox_66"]').die().live({
            select: function() {
                toggle('Add_mobiletextinput_71', 'mob', '');
            },
        });

        $('#Add_mobilecontainer [name="mobiletextinput_71"]').die().live({
            change: function() {
                onValueChanged();
                localStorage.setItem('rr', '');
            },
        });

        $('#Add_mobilecontainer [name="mobilebutton_103"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    onValueChanged();

                }
            },
        });

    }

    $("#Add").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        Add_beforeshow();
    });

    if (runBeforeShow) {
        Add_beforeshow();
    } else {
        Add_onLoad();
    }

}

$("#Add").die("pageinit").live("pageinit", function(event, ui) {
    Appery.processSelectMenu($(this));
    Add_js();
});